(function () {
    if (typeof window.jQuery === 'undefined') {
        console.warn('Summernote fallback: jQuery no está disponible.');
        return;
    }

    if (typeof jQuery.fn.summernote === 'undefined') {
        jQuery.fn.summernote = function () {
            console.warn('Summernote no pudo cargarse desde la CDN. Reemplace el archivo local con la librería oficial para habilitar el editor.');
            return this;
        };
    }
})();
